import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import api from '../../services/api';
import LoadingSpinner from '../common/LoadingSpinner';

const BookingWidget = ({ businessId, config = {} }) => {
  const [loading, setLoading] = useState(true);
  const [services, setServices] = useState([]);
  const [step, setStep] = useState(1);
  const [submitting, setSubmitting] = useState(false);

  const [formData, setFormData] = useState({
    serviceId: '',
    name: '',
    email: '',
    phone: '',
    date: '',
    time: '',
    notes: '',
  });

  // Default and custom styling
  const styles = {
    primaryColor: config.primaryColor || '#4f46e5',
    secondaryColor: config.secondaryColor || '#f3f4f6',
    fontFamily: config.fontFamily || 'sans-serif',
    borderRadius: config.borderRadius || '8px',
    ...config.styles
  };

  // Load services when component mounts
  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await api.getServices(businessId);
        setServices(response.data.services || []);
      } catch (error) {
        console.error('Error fetching services:', error);
        toast.error('Could not load available services');
      } finally {
        setLoading(false);
      }
    };

    fetchServices();
  }, [businessId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const nextStep = () => {
    if (step === 1 && !formData.serviceId) {
      toast.error('Please select a service to continue');
      return;
    }

    setStep(prev => prev + 1);
  };

  const prevStep = () => {
    setStep(prev => prev - 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Format date and time for API
      const startTime = new Date(`${formData.date}T${formData.time}`);
      const service = services.find(s => s._id === formData.serviceId);
      const duration = service?.duration || 60; // Default to 60 minutes
      const endTime = new Date(startTime.getTime() + duration * 60000);

      // First create or get customer
      const customerData = {
        name: formData.name,
        email: formData.email,
        phone: formData.phone
      };

      const customerResponse = await api.createOrUpdateCustomer(customerData);
      const customerId = customerResponse.data.customer?._id;

      if (!customerId) {
        throw new Error('Failed to create customer record');
      }

      // Create the booking
      const bookingData = {
        service: formData.serviceId,
        client: customerId,
        businessId: businessId,
        startTime: startTime.toISOString(),
        endTime: endTime.toISOString(),
        notes: {
          client: formData.notes,
        },
        status: 'pending',
        source: 'widget'
      };

      await api.createBooking(bookingData);

      // Move to success screen
      setStep(4);

    } catch (error) {
      console.error('Error creating appointment:', error);
      toast.error(error.response?.data?.error || 'Failed to book appointment');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="booking-widget-container" style={{
        fontFamily: styles.fontFamily,
        maxWidth: '100%',
        width: '400px',
        margin: '0 auto',
        padding: '20px',
        borderRadius: styles.borderRadius,
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        backgroundColor: '#fff'
      }}>
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="booking-widget-container" style={{
      fontFamily: styles.fontFamily,
      maxWidth: '100%',
      width: '400px',
      margin: '0 auto',
      padding: '20px',
      borderRadius: styles.borderRadius,
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      backgroundColor: '#fff'
    }}>
      <div className="booking-widget-header" style={{
        textAlign: 'center',
        marginBottom: '20px'
      }}>
        <h2 style={{ color: styles.primaryColor, margin: '0 0 10px 0' }}>
          {config.title || 'Book an Appointment'}
        </h2>
        {config.logoUrl && (
          <img 
            src={config.logoUrl} 
            alt="Business Logo" 
            style={{ maxWidth: '120px', marginBottom: '10px' }} 
          />
        )}
      </div>

      {/* Progress Steps */}
      <div className="booking-progress" style={{
        display: 'flex',
        justifyContent: 'space-between',
        marginBottom: '20px'
      }}>
        {[1, 2, 3].map(stepNum => (
          <div 
            key={stepNum}
            style={{
              width: '30px',
              height: '30px',
              borderRadius: '50%',
              backgroundColor: step >= stepNum ? styles.primaryColor : styles.secondaryColor,
              color: step >= stepNum ? '#fff' : '#333',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontWeight: 'bold'
            }}
          >
            {stepNum}
          </div>
        ))}
      </div>

      {/* Step 1: Service Selection */}
      {step === 1 && (
        <div className="booking-step">
          <h3 style={{ color: '#333', marginBottom: '15px' }}>Select a Service</h3>

          <div className="services-list">
            {services.length === 0 ? (
              <p>No services available at this time.</p>
            ) : (
              services.map(service => (
                <div 
                  key={service._id}
                  className="service-item"
                  style={{
                    padding: '15px',
                    marginBottom: '10px',
                    border: `2px solid ${formData.serviceId === service._id ? styles.primaryColor : '#e5e7eb'}`,
                    borderRadius: styles.borderRadius,
                    cursor: 'pointer',
                    backgroundColor: formData.serviceId === service._id ? `${styles.primaryColor}15` : '#fff'
                  }}
                  onClick={() => setFormData(prev => ({ ...prev, serviceId: service._id }))}
                >
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <h4 style={{ margin: '0 0 5px 0' }}>{service.title}</h4>
                    <span style={{ fontWeight: 'bold' }}>${service.price?.amount || 0}</span>
                  </div>
                  <p style={{ margin: '5px 0', fontSize: '14px' }}>{service.description}</p>
                  <div style={{ fontSize: '13px', color: '#666' }}>
                    <span>{service.duration} min</span>
                  </div>
                </div>
              ))
            )}
          </div>

          <div className="booking-actions" style={{ marginTop: '20px' }}>
            <button 
              onClick={nextStep}
              disabled={!formData.serviceId || services.length === 0}
              style={{
                backgroundColor: styles.primaryColor,
                color: '#fff',
                border: 'none',
                padding: '10px 20px',
                borderRadius: styles.borderRadius,
                cursor: 'pointer',
                width: '100%',
                opacity: (!formData.serviceId || services.length === 0) ? 0.7 : 1
              }}
            >
              Continue
            </button>
          </div>
        </div>
      )}

      {/* Step 2: Customer Information */}
      {step === 2 && (
        <div className="booking-step">
          <h3 style={{ color: '#333', marginBottom: '15px' }}>Your Information</h3>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label htmlFor="name" style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>Name</label>
            <input 
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: styles.borderRadius,
                border: '1px solid #ddd',
                fontSize: '16px'
              }}
              placeholder="Your full name"
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label htmlFor="email" style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>Email</label>
            <input 
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: styles.borderRadius,
                border: '1px solid #ddd',
                fontSize: '16px'
              }}
              placeholder="Your email address"
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label htmlFor="phone" style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>Phone Number</label>
            <input 
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: styles.borderRadius,
                border: '1px solid #ddd',
                fontSize: '16px'
              }}
              placeholder="Your phone number"
            />
          </div>

          <div className="booking-actions" style={{ 
            marginTop: '20px',
            display: 'flex',
            justifyContent: 'space-between'
          }}>
            <button 
              onClick={prevStep}
              style={{
                backgroundColor: styles.secondaryColor,
                color: '#333',
                border: 'none',
                padding: '10px 20px',
                borderRadius: styles.borderRadius,
                cursor: 'pointer',
                width: '48%'
              }}
            >
              Back
            </button>
            <button 
              onClick={nextStep}
              disabled={!formData.name || !formData.email}
              style={{
                backgroundColor: styles.primaryColor,
                color: '#fff',
                border: 'none',
                padding: '10px 20px',
                borderRadius: styles.borderRadius,
                cursor: 'pointer',
                width: '48%',
                opacity: (!formData.name || !formData.email) ? 0.7 : 1
              }}
            >
              Continue
            </button>
          </div>
        </div>
      )}

      {/* Step 3: Date & Time Selection */}
      {step === 3 && (
        <div className="booking-step">
          <h3 style={{ color: '#333', marginBottom: '15px' }}>Choose Date & Time</h3>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label htmlFor="date" style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>Date</label>
            <input 
              type="date"
              id="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              required
              min={new Date().toISOString().split('T')[0]}
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: styles.borderRadius,
                border: '1px solid #ddd',
                fontSize: '16px'
              }}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label htmlFor="time" style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>Time</label>
            <input 
              type="time"
              id="time"
              name="time"
              value={formData.time}
              onChange={handleInputChange}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: styles.borderRadius,
                border: '1px solid #ddd',
                fontSize: '16px'
              }}
            />
          </div>

          <div className="form-group" style={{ marginBottom: '15px' }}>
            <label htmlFor="notes" style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>Additional Notes</label>
            <textarea
              id="notes"
              name="notes"
              value={formData.notes}
              onChange={handleInputChange}
              rows="3"
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: styles.borderRadius,
                border: '1px solid #ddd',
                fontSize: '16px',
                resize: 'vertical'
              }}
              placeholder="Any special instructions or requests..."
            />
          </div>

          <div className="booking-actions" style={{ 
            marginTop: '20px',
            display: 'flex',
            justifyContent: 'space-between'
          }}>
            <button 
              onClick={prevStep}
              style={{
                backgroundColor: styles.secondaryColor,
                color: '#333',
                border: 'none',
                padding: '10px 20px',
                borderRadius: styles.borderRadius,
                cursor: 'pointer',
                width: '48%'
              }}
            >
              Back
            </button>
            <button 
              onClick={handleSubmit}
              disabled={!formData.date || !formData.time || submitting}
              style={{
                backgroundColor: styles.primaryColor,
                color: '#fff',
                border: 'none',
                padding: '10px 20px',
                borderRadius: styles.borderRadius,
                cursor: 'pointer',
                width: '48%',
                opacity: (!formData.date || !formData.time || submitting) ? 0.7 : 1
              }}
            >
              {submitting ? 'Booking...' : 'Book Appointment'}
            </button>
          </div>
        </div>
      )}

      {/* Step 4: Confirmation */}
      {step === 4 && (
        <div className="booking-step confirmation" style={{ textAlign: 'center' }}>
          <div style={{ 
            width: '60px', 
            height: '60px', 
            borderRadius: '50%', 
            backgroundColor: '#4CAF50',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '30px',
            margin: '0 auto 20px'
          }}>
            ✓
          </div>

          <h3 style={{ color: '#333', marginBottom: '15px' }}>Booking Confirmed!</h3>
          <p style={{ marginBottom: '20px' }}>
            Your appointment has been scheduled. You will receive a confirmation email shortly.
          </p>

          <div className="booking-summary" style={{
            backgroundColor: styles.secondaryColor,
            padding: '15px',
            borderRadius: styles.borderRadius,
            marginBottom: '20px',
            textAlign: 'left'
          }}>
            <p><strong>Service:</strong> {services.find(s => s._id === formData.serviceId)?.title}</p>
            <p><strong>Date:</strong> {new Date(formData.date).toLocaleDateString()}</p>
            <p><strong>Time:</strong> {formData.time}</p>
          </div>

          <button 
            onClick={() => {
              // Reset form and go back to step 1
              setFormData({
                serviceId: '',
                name: '',
                email: '',
                phone: '',
                date: '',
                time: '',
                notes: '',
              });
              setStep(1);
            }}
            style={{
              backgroundColor: styles.primaryColor,
              color: '#fff',
              border: 'none',
              padding: '10px 20px',
              borderRadius: styles.borderRadius,
              cursor: 'pointer',
              width: '100%'
            }}
          >
            Book Another Appointment
          </button>
        </div>
      )}

      <div className="booking-widget-footer" style={{
        marginTop: '20px',
        textAlign: 'center',
        fontSize: '12px',
        color: '#666'
      }}>
        Powered by DogServices Booking System
      </div>
    </div>
  );
};

export default BookingWidget;
