import React, { useState, useEffect } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { toast } from 'react-toastify';

const WidgetEmbed = ({ config, businessId }) => {
  const [copied, setCopied] = useState(false);
  const [selectedTab, setSelectedTab] = useState('inline');

  // Reset copied status after 3 seconds
  useEffect(() => {
    if (copied) {
      const timer = setTimeout(() => {
        setCopied(false);
      }, 3000);

      return () => clearTimeout(timer);
    }
  }, [copied]);

  // Generate the embed script
  const generateEmbedCode = () => {
    const serializedConfig = encodeURIComponent(JSON.stringify(config));

    const inlineScript = `
<div id="dog-services-booking-widget"></div>
<script src="${window.location.origin}/widget.js"></script>
<script>
  window.dogServicesWidget.initialize({
    businessId: "${businessId}",
    containerId: "dog-services-booking-widget",
    config: ${JSON.stringify(config)}
  });
</script>
    `.trim();

    const popupScript = `
<button id="open-booking-widget">Book Now</button>
<script src="${window.location.origin}/widget.js"></script>
<script>
  window.dogServicesWidget.initialize({
    businessId: "${businessId}",
    mode: "popup",
    buttonId: "open-booking-widget",
    config: ${JSON.stringify(config)}
  });
</script>
    `.trim();

    return {
      inline: inlineScript,
      popup: popupScript
    };
  };

  const handleCopy = () => {
    setCopied(true);
    toast.success('Code copied to clipboard!');
  };

  const embedCodes = generateEmbedCode();

  return (
    <div className="widget-embed-container">
      <div className="embed-tabs" style={{ 
        display: 'flex',
        borderBottom: '1px solid #e5e7eb',
        marginBottom: '20px'
      }}>
        <div 
          className={`embed-tab ${selectedTab === 'inline' ? 'active' : ''}`} 
          onClick={() => setSelectedTab('inline')}
          style={{
            padding: '10px 20px',
            cursor: 'pointer',
            borderBottom: selectedTab === 'inline' ? '2px solid #4f46e5' : '2px solid transparent',
            fontWeight: selectedTab === 'inline' ? 'bold' : 'normal'
          }}
        >
          Inline Widget
        </div>
        <div 
          className={`embed-tab ${selectedTab === 'popup' ? 'active' : ''}`} 
          onClick={() => setSelectedTab('popup')}
          style={{
            padding: '10px 20px',
            cursor: 'pointer',
            borderBottom: selectedTab === 'popup' ? '2px solid #4f46e5' : '2px solid transparent',
            fontWeight: selectedTab === 'popup' ? 'bold' : 'normal'
          }}
        >
          Popup Widget
        </div>
      </div>

      <div className="embed-content">
        {selectedTab === 'inline' && (
          <div className="inline-embed">
            <p style={{ marginBottom: '10px' }}>
              Copy and paste this code where you want the booking widget to appear on your website:
            </p>
          </div>
        )}

        {selectedTab === 'popup' && (
          <div className="popup-embed">
            <p style={{ marginBottom: '10px' }}>
              Copy and paste this code to add a "Book Now" button that will open the booking widget in a popup:
            </p>
          </div>
        )}

        <div className="code-container" style={{
          backgroundColor: '#f8f9fa',
          padding: '15px',
          borderRadius: '8px',
          border: '1px solid #e5e7eb',
          position: 'relative',
          marginBottom: '15px'
        }}>
          <pre style={{
            margin: 0,
            whiteSpace: 'pre-wrap',
            wordBreak: 'break-word',
            fontFamily: 'monospace',
            fontSize: '14px',
            maxHeight: '300px',
            overflowY: 'auto'
          }}>
            {selectedTab === 'inline' ? embedCodes.inline : embedCodes.popup}
          </pre>

          <CopyToClipboard 
            text={selectedTab === 'inline' ? embedCodes.inline : embedCodes.popup}
            onCopy={handleCopy}
          >
            <button style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '5px 10px',
              backgroundColor: copied ? '#10b981' : '#4f46e5',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              fontSize: '12px',
              cursor: 'pointer'
            }}>
              {copied ? 'Copied!' : 'Copy'}
            </button>
          </CopyToClipboard>
        </div>

        <div className="integration-instructions" style={{
          backgroundColor: '#f0f7ff',
          padding: '15px',
          borderRadius: '8px',
          border: '1px solid #e1effe',
          fontSize: '14px'
        }}>
          <h4 style={{ marginTop: 0, marginBottom: '10px' }}>Integration Instructions</h4>
          <ol style={{ paddingLeft: '20px', margin: 0 }}>
            <li style={{ marginBottom: '5px' }}>Copy the code above.</li>
            <li style={{ marginBottom: '5px' }}>Paste it into your website's HTML where you want the {selectedTab === 'inline' ? 'booking widget to appear' : '"Book Now" button to appear'}.</li>
            <li>Save your changes and publish your website.</li>
          </ol>

          <div style={{ marginTop: '15px', fontSize: '13px', color: '#4b5563' }}>
            <strong>Note:</strong> This widget will directly connect to your business account. Any bookings made through it will appear in your dashboard.
          </div>
        </div>
      </div>
    </div>
  );
};

export default WidgetEmbed;
