import React, { useState, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import { toast } from 'react-toastify';
import PageHeader from '../components/common/PageHeader';
import LoadingSpinner from '../components/common/LoadingSpinner';
import WidgetEmbed from '../components/widget/WidgetEmbed';
import BookingWidget from '../components/widget/BookingWidget';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useBusinessContext } from '../context/BusinessContext';

const WidgetSetup = () => {
  const { currentUser } = useAuth();
  const { currentBusiness } = useBusinessContext();
  const businessId = currentBusiness?._id;

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [activeTab, setActiveTab] = useState('appearance');

  const [widgetConfig, setWidgetConfig] = useState({
    title: 'Book an Appointment',
    primaryColor: '#4f46e5',
    secondaryColor: '#f3f4f6',
    fontFamily: 'sans-serif',
    borderRadius: '8px',
    logoUrl: '',
    servicesEnabled: true,
    requirePhone: false,
    emailNotifications: true,
    showPrices: true,
    allowNotes: true
  });

  useEffect(() => {
    const fetchWidgetSettings = async () => {
      try {
        // Try to load existing widget settings
        const response = await api.getWidgetSettings(businessId);
        if (response.data && response.data.config) {
          setWidgetConfig(response.data.config);
        }
      } catch (error) {
        console.log('No existing widget settings found, using defaults');
      } finally {
        setLoading(false);
      }
    };

    if (businessId) {
      fetchWidgetSettings();
    } else {
      setLoading(false);
    }
  }, [businessId]);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setWidgetConfig(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleColorChange = (color, propertyName) => {
    setWidgetConfig(prev => ({
      ...prev,
      [propertyName]: color.hex
    }));
  };

  const saveWidgetSettings = async () => {
    if (!businessId) {
      toast.error('Business information is missing');
      return;
    }

    setSaving(true);
    try {
      await api.saveWidgetSettings(businessId, { config: widgetConfig });
      toast.success('Widget settings saved successfully');
    } catch (error) {
      console.error('Error saving widget settings:', error);
      toast.error('Failed to save widget settings');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="page-container widget-setup-page">
      <PageHeader 
        title="Booking Widget Setup" 
        description="Configure and get code to embed a booking widget on your website"
      />

      <div className="card">
        <div className="card-header" style={{ borderBottom: '1px solid #e5e7eb' }}>
          <ul className="nav nav-tabs">
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'appearance' ? 'active' : ''}`}
                onClick={() => setActiveTab('appearance')}
                style={{
                  padding: '12px 16px',
                  border: 'none',
                  background: 'none',
                  fontWeight: activeTab === 'appearance' ? 'bold' : 'normal',
                  borderBottom: activeTab === 'appearance' ? '2px solid #4f46e5' : 'none',
                  cursor: 'pointer'
                }}
              >
                Appearance
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'settings' ? 'active' : ''}`}
                onClick={() => setActiveTab('settings')}
                style={{
                  padding: '12px 16px',
                  border: 'none',
                  background: 'none',
                  fontWeight: activeTab === 'settings' ? 'bold' : 'normal',
                  borderBottom: activeTab === 'settings' ? '2px solid #4f46e5' : 'none',
                  cursor: 'pointer'
                }}
              >
                Settings
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'preview' ? 'active' : ''}`}
                onClick={() => setActiveTab('preview')}
                style={{
                  padding: '12px 16px',
                  border: 'none',
                  background: 'none',
                  fontWeight: activeTab === 'preview' ? 'bold' : 'normal',
                  borderBottom: activeTab === 'preview' ? '2px solid #4f46e5' : 'none',
                  cursor: 'pointer'
                }}
              >
                Preview
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'embed' ? 'active' : ''}`}
                onClick={() => setActiveTab('embed')}
                style={{
                  padding: '12px 16px',
                  border: 'none',
                  background: 'none',
                  fontWeight: activeTab === 'embed' ? 'bold' : 'normal',
                  borderBottom: activeTab === 'embed' ? '2px solid #4f46e5' : 'none',
                  cursor: 'pointer'
                }}
              >
                Get Embed Code
              </button>
            </li>
          </ul>
        </div>

        <div className="card-body">
          {/* Appearance Tab */}
          {activeTab === 'appearance' && (
            <div className="appearance-tab">
              <div className="form-section">
                <h3>Branding</h3>

                <div className="form-group" style={{ marginBottom: '20px' }}>
                  <label htmlFor="title">Widget Title</label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    className="form-control"
                    value={widgetConfig.title}
                    onChange={handleInputChange}
                  />
                  <small className="form-text text-muted">
                    The title displayed at the top of the booking widget
                  </small>
                </div>

                <div className="form-group" style={{ marginBottom: '20px' }}>
                  <label htmlFor="logoUrl">Logo URL</label>
                  <input
                    type="text"
                    id="logoUrl"
                    name="logoUrl"
                    className="form-control"
                    value={widgetConfig.logoUrl}
                    onChange={handleInputChange}
                    placeholder="https://example.com/your-logo.png"
                  />
                  <small className="form-text text-muted">
                    URL to your business logo image (optional)
                  </small>
                </div>
              </div>

              <div className="form-section">
                <h3>Colors</h3>

                <div className="color-pickers" style={{ display: 'flex', gap: '30px', marginBottom: '20px' }}>
                  <div className="color-picker">
                    <label>Primary Color</label>
                    <div style={{ marginTop: '10px' }}>
                      <ChromePicker
                        color={widgetConfig.primaryColor}
                        onChangeComplete={(color) => handleColorChange(color, 'primaryColor')}
                        disableAlpha={true}
                      />
                    </div>
                    <div style={{ marginTop: '10px' }}>
                      <input
                        type="text"
                        className="form-control"
                        value={widgetConfig.primaryColor}
                        onChange={(e) => setWidgetConfig(prev => ({ ...prev, primaryColor: e.target.value }))}
                        style={{ width: '100%' }}
                      />
                    </div>
                  </div>

                  <div className="color-picker">
                    <label>Secondary Color</label>
                    <div style={{ marginTop: '10px' }}>
                      <ChromePicker
                        color={widgetConfig.secondaryColor}
                        onChangeComplete={(color) => handleColorChange(color, 'secondaryColor')}
                        disableAlpha={true}
                      />
                    </div>
                    <div style={{ marginTop: '10px' }}>
                      <input
                        type="text"
                        className="form-control"
                        value={widgetConfig.secondaryColor}
                        onChange={(e) => setWidgetConfig(prev => ({ ...prev, secondaryColor: e.target.value }))}
                        style={{ width: '100%' }}
                      />
                    </div>
                  </div>
                </div>
              </div>

              <div className="form-section">
                <h3>Typography & Layout</h3>

                <div className="form-group" style={{ marginBottom: '20px' }}>
                  <label htmlFor="fontFamily">Font Family</label>
                  <select
                    id="fontFamily"
                    name="fontFamily"
                    className="form-control"
                    value={widgetConfig.fontFamily}
                    onChange={handleInputChange}
                  >
                    <option value="sans-serif">Sans-serif</option>
                    <option value="serif">Serif</option>
                    <option value="monospace">Monospace</option>
                    <option value="'Arial', sans-serif">Arial</option>
                    <option value="'Helvetica', sans-serif">Helvetica</option>
                    <option value="'Georgia', serif">Georgia</option>
                    <option value="'Verdana', sans-serif">Verdana</option>
                    <option value="'Tahoma', sans-serif">Tahoma</option>
                  </select>
                </div>

                <div className="form-group" style={{ marginBottom: '20px' }}>
                  <label htmlFor="borderRadius">Border Radius</label>
                  <select
                    id="borderRadius"
                    name="borderRadius"
                    className="form-control"
                    value={widgetConfig.borderRadius}
                    onChange={handleInputChange}
                  >
                    <option value="0">Square (0px)</option>
                    <option value="4px">Slight (4px)</option>
                    <option value="8px">Rounded (8px)</option>
                    <option value="12px">More Rounded (12px)</option>
                    <option value="16px">Very Rounded (16px)</option>
                    <option value="24px">Pill-like (24px)</option>
                  </select>
                </div>
              </div>
            </div>
          )}

          {/* Settings Tab */}
          {activeTab === 'settings' && (
            <div className="settings-tab">
              <div className="form-section">
                <h3>Widget Settings</h3>

                <div className="form-check" style={{ marginBottom: '15px' }}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="servicesEnabled"
                    name="servicesEnabled"
                    checked={widgetConfig.servicesEnabled}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="servicesEnabled">
                    Display All Services
                  </label>
                  <small className="form-text text-muted d-block">
                    When enabled, all active services will be available in the widget.
                    If disabled, you can select specific services below.
                  </small>
                </div>

                <div className="form-check" style={{ marginBottom: '15px' }}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="requirePhone"
                    name="requirePhone"
                    checked={widgetConfig.requirePhone}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="requirePhone">
                    Require Phone Number
                  </label>
                  <small className="form-text text-muted d-block">
                    When enabled, customers must provide a phone number when booking.
                  </small>
                </div>

                <div className="form-check" style={{ marginBottom: '15px' }}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="emailNotifications"
                    name="emailNotifications"
                    checked={widgetConfig.emailNotifications}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="emailNotifications">
                    Send Email Notifications
                  </label>
                  <small className="form-text text-muted d-block">
                    When enabled, customers will receive email confirmations for their bookings.
                  </small>
                </div>

                <div className="form-check" style={{ marginBottom: '15px' }}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="showPrices"
                    name="showPrices"
                    checked={widgetConfig.showPrices}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="showPrices">
                    Show Service Prices
                  </label>
                  <small className="form-text text-muted d-block">
                    When enabled, service prices will be displayed in the widget.
                  </small>
                </div>

                <div className="form-check" style={{ marginBottom: '15px' }}>
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="allowNotes"
                    name="allowNotes"
                    checked={widgetConfig.allowNotes}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="allowNotes">
                    Allow Customer Notes
                  </label>
                  <small className="form-text text-muted d-block">
                    When enabled, customers can add notes to their booking.
                  </small>
                </div>
              </div>
            </div>
          )}

          {/* Preview Tab */}
          {activeTab === 'preview' && (
            <div className="preview-tab">
              <h3>Widget Preview</h3>
              <p className="text-muted">
                This is how your booking widget will appear to your customers.
              </p>

              <div className="widget-preview-container" style={{ 
                padding: '20px',
                backgroundColor: '#f9fafb',
                borderRadius: '8px',
                display: 'flex',
                justifyContent: 'center'
              }}>
                <BookingWidget 
                  businessId={businessId}
                  config={widgetConfig}
                />
              </div>
            </div>
          )}

          {/* Embed Tab */}
          {activeTab === 'embed' && (
            <div className="embed-tab">
              <h3>Website Integration</h3>
              <p className="text-muted">
                Add this booking widget to your website by copying the code below.
              </p>

              <WidgetEmbed 
                config={widgetConfig}
                businessId={businessId}
              />
            </div>
          )}

          {(activeTab === 'appearance' || activeTab === 'settings') && (
            <div className="form-actions" style={{ marginTop: '30px' }}>
              <button 
                className="btn btn-primary"
                onClick={saveWidgetSettings}
                disabled={saving}
              >
                {saving ? 'Saving...' : 'Save Settings'}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WidgetSetup;
