import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import BookingWidget from '../components/widget/BookingWidget';

const WidgetEmbed = () => {
  const [searchParams] = useSearchParams();
  const [config, setConfig] = useState({});
  const businessId = searchParams.get('businessId');

  useEffect(() => {
    // Get configuration from URL params
    try {
      const configParam = searchParams.get('config');
      if (configParam) {
        const parsedConfig = JSON.parse(configParam);
        setConfig(parsedConfig);
      }
    } catch (error) {
      console.error('Error parsing widget configuration:', error);
    }

    // Setup messaging for cross-origin communication
    window.addEventListener('message', handleMessage);

    // Clean up listener
    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, [searchParams]);

  // Handle incoming messages from parent window
  const handleMessage = (event) => {
    // Add message handling as needed
    // This is for future functionalities like resizing, event notifications, etc.
  };

  // Send message to parent window
  const sendMessage = (message) => {
    if (window.parent !== window) {
      window.parent.postMessage(message, '*');
    }
  };

  if (!businessId) {
    return (
      <div className="widget-error" style={{ padding: '20px', textAlign: 'center' }}>
        <h3 style={{ color: '#e53e3e' }}>Configuration Error</h3>
        <p>Missing required business information.</p>
      </div>
    );
  }

  return (
    <div className="widget-embed-container" style={{ padding: '10px' }}>
      <BookingWidget 
        businessId={businessId}
        config={config}
      />
    </div>
  );
};

export default WidgetEmbed;
