            type: 'event'
          },
          {
            id: 'appt-5',
            title: 'Auditorium - Community Forum',
            customer: 'Michael Brown',
            date: '2025-03-04',
            startTime: '11:00',
            endTime: '12:30',
            status: 'confirmed',
            type: 'meeting'
          },
          {
            id: 'appt-6',
            title: 'Waterway Section A - Boat Race',
            customer: 'Jennifer Taylor',
            date: '2025-03-05',
