import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Context Providers
import { AuthProvider, useAuth } from './context/AuthContext';

// Layout Components
import Sidebar from './components/layout/Sidebar';
import TopNav from './components/layout/TopNav';

// Page Components
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';
import BusinessDashboard from './pages/BusinessDashboard';
import ScheduleCalendar from './pages/ScheduleCalendar';
import AppointmentsList from './pages/AppointmentsList';
import AppointmentDetail from './pages/AppointmentDetail';
import CustomersList from './pages/CustomersList';
import CustomerDetail from './pages/CustomerDetail';
import PetsList from './pages/PetsList';
import ServicesList from './pages/ServicesList';
import ServiceDetail from './pages/ServiceDetail';
import WidgetIntegration from './pages/WidgetIntegration';
import Settings from './pages/Settings';
import Profile from './pages/Profile';
import NotFound from './pages/NotFound';

// Widget Preview
import WidgetPreview from './pages/WidgetPreview';

// Protected Route Component
import ProtectedRoute from './components/common/ProtectedRoute';

// Styles
import './styles/business-portal.css';

function AppContent() {
  const { isAuthenticated, authLoading, setBypassAuth } = useAuth();
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarCollapsed(!isSidebarCollapsed);
  };

  const handleBypassLogin = () => {
    // Set a demo user for bypass login
    setBypassAuth({
      userId: 'demo-user',
      name: 'Demo Business',
      email: 'demo@example.com',
      role: 'service_provider'
    });
  };

  if (authLoading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading application...</p>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <ToastContainer position="top-right" autoClose={3000} />
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/widget-preview" element={<WidgetPreview />} />
          <Route path="/" element={<Home />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    );
  }

  return (
    <div className="business-portal">
      <ToastContainer position="top-right" autoClose={3000} />
      <Sidebar collapsed={isSidebarCollapsed} />

      <div className={`main-container ${isSidebarCollapsed ? 'expanded' : ''}`}>
        <TopNav toggleSidebar={toggleSidebar} />

        <div className="content-container">
          <Routes>
            <Route path="/" element={<Navigate to="/dashboard" />} />

            {/* Business Routes */}
            <Route path="/dashboard" element={<BusinessDashboard />} />
            <Route path="/calendar" element={<ScheduleCalendar />} />
            <Route path="/appointments" element={<AppointmentsList />} />
            <Route path="/appointments/:appointmentId" element={<AppointmentDetail />} />
            <Route path="/customers" element={<CustomersList />} />
            <Route path="/customers/:customerId" element={<CustomerDetail />} />
            <Route path="/pets" element={<PetsList />} />
            <Route path="/services" element={<ServicesList />} />
            <Route path="/services/:serviceId" element={<ServiceDetail />} />
            <Route path="/widget-integration" element={<WidgetIntegration />} />
            <Route path="/widget-preview" element={<WidgetPreview />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="/profile" element={<Profile />} />

            {/* 404 Route */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
