import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import api from '../services/api';

// Simple standalone loading spinner
const StandaloneLoadingSpinner = () => (
  <div style={{ 
    display: 'flex', 
    justifyContent: 'center', 
    alignItems: 'center', 
    height: '100vh',
    width: '100%'
  }}>
    <div style={{ 
      border: '4px solid #f3f3f3',
      borderTop: '4px solid #4f46e5', 
      borderRadius: '50%',
      width: '40px',
      height: '40px',
      animation: 'spin 1s linear infinite'
    }}></div>
    <style>{`
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
    `}</style>
  </div>
);

const BookingPage = () => {
  const [searchParams] = useSearchParams();
  const businessId = searchParams.get('businessId') || 'default';

  const [loading, setLoading] = useState(true);
  const [services, setServices] = useState([]);
  const [step, setStep] = useState(1);
  const [submitting, setSubmitting] = useState(false);

  const [formData, setFormData] = useState({
    serviceId: '',
    name: '',
    email: '',
    phone: '',
    date: '',
    time: '',
    notes: '',
  });

  // Load business info and services
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch services for this business
        const servicesResponse = await api.getServices({ businessId });
        setServices(servicesResponse.data.services || []);
      } catch (error) {
        console.error('Error fetching booking data:', error);
        toast.error('Could not load services. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [businessId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const nextStep = () => {
    if (step === 1 && !formData.serviceId) {
      toast.error('Please select a service to continue');
      return;
    }

    if (step === 2 && (!formData.name || !formData.email)) {
      toast.error('Please provide your name and email');
      return;
    }

    setStep(prev => prev + 1);
  };

  const prevStep = () => {
    setStep(prev => prev - 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Format date and time for API
      const startTime = new Date(`${formData.date}T${formData.time}`);
      const service = services.find(s => s._id === formData.serviceId);
      const duration = service?.duration || 60; // Default to 60 minutes
      const endTime = new Date(startTime.getTime() + duration * 60000);

      // First create or get customer
      const customerData = {
        name: formData.name,
        email: formData.email,
        phone: formData.phone
      };

      const customerResponse = await api.createOrUpdateCustomer(customerData);
      const customerId = customerResponse.data.customer?._id;

      if (!customerId) {
        throw new Error('Failed to create customer record');
      }

      // Create the booking
      const bookingData = {
        service: formData.serviceId,
        client: customerId,
        businessId: businessId,
        startTime: startTime.toISOString(),
        endTime: endTime.toISOString(),
        notes: {
          client: formData.notes,
        },
        status: 'pending',
        source: 'booking_page'
      };

      await api.createBooking(bookingData);

      // Move to success screen
      setStep(4);

    } catch (error) {
      console.error('Error creating appointment:', error);
      toast.error(error.response?.data?.error || 'Failed to book appointment');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="booking-page-container" style={{
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      fontFamily: 'sans-serif'
    }}>
      <div className="booking-header" style={{
        textAlign: 'center',
        marginBottom: '30px'
      }}>
        <h1 style={{ 
          color: '#4f46e5', 
          fontSize: '28px',
          marginBottom: '10px'
        }}>Book an Appointment</h1>
        <p style={{ color: '#666', marginBottom: '20px' }}>
          Follow the steps below to schedule your service
        </p>
      </div>

      {/* Progress Steps */}
      <div className="booking-progress" style={{
        display: 'flex',
        justifyContent: 'space-between',
        marginBottom: '30px'
      }}>
        {[1, 2, 3].map(stepNum => (
          <div 
            key={stepNum}
            style={{
              width: '70px',
              textAlign: 'center'
            }}
          >
            <div style={{
              width: '40px',
              height: '40px',
              borderRadius: '50%',
              backgroundColor: step >= stepNum ? '#4f46e5' : '#e5e7eb',
              color: step >= stepNum ? '#fff' : '#333',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontWeight: 'bold',
              margin: '0 auto 10px'
            }}>
              {stepNum}
            </div>
            <div style={{ fontSize: '14px' }}>
              {stepNum === 1 ? 'Service' : stepNum === 2 ? 'Information' : 'Schedule'}
            </div>
          </div>
        ))}
      </div>

      <div className="booking-card" style={{
        backgroundColor: 'white',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        padding: '30px'
      }}>
        {/* Step 1: Service Selection */}
        {step === 1 && (
          <div className="booking-step">
            <h2 style={{ marginBottom: '20px', fontSize: '20px' }}>Select a Service</h2>

            <div className="services-list">
              {services.length === 0 ? (
                <p>No services available at this time.</p>
              ) : (
                services.map(service => (
                  <div 
                    key={service._id}
                    className="service-item"
                    style={{
                      padding: '15px',
                      marginBottom: '15px',
                      border: `2px solid ${formData.serviceId === service._id ? '#4f46e5' : '#e5e7eb'}`,
                      borderRadius: '8px',
                      cursor: 'pointer',
                      backgroundColor: formData.serviceId === service._id ? '#f5f3ff' : '#fff'
                    }}
                    onClick={() => setFormData(prev => ({ ...prev, serviceId: service._id }))}
                  >
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <h3 style={{ margin: '0 0 5px 0', fontSize: '18px' }}>{service.title}</h3>
                      <span style={{ fontWeight: 'bold' }}>${service.price?.amount || 0}</span>
                    </div>
                    <p style={{ margin: '5px 0', fontSize: '14px', color: '#4b5563' }}>{service.description}</p>
                    <div style={{ fontSize: '13px', color: '#6b7280', marginTop: '8px' }}>
                      <span>Duration: {service.duration} min</span>
                    </div>
                  </div>
                ))
              )}
            </div>

            <div className="booking-actions" style={{ marginTop: '30px' }}>
              <button 
                onClick={nextStep}
                disabled={!formData.serviceId || services.length === 0}
                style={{
                  backgroundColor: '#4f46e5',
                  color: '#fff',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '100%',
                  fontSize: '16px',
                  fontWeight: '500',
                  opacity: (!formData.serviceId || services.length === 0) ? 0.6 : 1
                }}
              >
                Continue to Your Information
              </button>
            </div>
          </div>
        )}

        {/* Step 2: Customer Information */}
        {step === 2 && (
          <div className="booking-step">
            <h2 style={{ marginBottom: '20px', fontSize: '20px' }}>Your Information</h2>

            <div className="form-group" style={{ marginBottom: '20px' }}>
              <label htmlFor="name" style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>Name *</label>
              <input 
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  boxSizing: 'border-box'
                }}
                placeholder="Your full name"
              />
            </div>

            <div className="form-group" style={{ marginBottom: '20px' }}>
              <label htmlFor="email" style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>Email *</label>
              <input 
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  boxSizing: 'border-box'
                }}
                placeholder="Your email address"
              />
            </div>

            <div className="form-group" style={{ marginBottom: '20px' }}>
              <label htmlFor="phone" style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>Phone Number</label>
              <input 
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  boxSizing: 'border-box'
                }}
                placeholder="Your phone number"
              />
            </div>

            <div className="booking-actions" style={{ 
              marginTop: '30px',
              display: 'flex',
              justifyContent: 'space-between'
            }}>
              <button 
                onClick={prevStep}
                style={{
                  backgroundColor: '#f3f4f6',
                  color: '#374151',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '48%',
                  fontSize: '16px',
                  fontWeight: '500'
                }}
              >
                Back
              </button>
              <button 
                onClick={nextStep}
                disabled={!formData.name || !formData.email}
                style={{
                  backgroundColor: '#4f46e5',
                  color: '#fff',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '48%',
                  fontSize: '16px',
                  fontWeight: '500',
                  opacity: (!formData.name || !formData.email) ? 0.6 : 1
                }}
              >
                Continue to Schedule
              </button>
            </div>
          </div>
        )}

        {/* Step 3: Date & Time Selection */}
        {step === 3 && (
          <div className="booking-step">
            <h2 style={{ marginBottom: '20px', fontSize: '20px' }}>Choose Date & Time</h2>

            <div className="form-group" style={{ marginBottom: '20px' }}>
              <label htmlFor="date" style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>Date *</label>
              <input 
                type="date"
                id="date"
                name="date"
                value={formData.date}
                onChange={handleInputChange}
                required
                min={new Date().toISOString().split('T')[0]}
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  boxSizing: 'border-box'
                }}
              />
            </div>

            <div className="form-group" style={{ marginBottom: '20px' }}>
              <label htmlFor="time" style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>Time *</label>
              <input 
                type="time"
                id="time"
                name="time"
                value={formData.time}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  boxSizing: 'border-box'
                }}
              />
            </div>

            <div className="form-group" style={{ marginBottom: '20px' }}>
              <label htmlFor="notes" style={{ display: 'block', marginBottom: '8px', fontWeight: '500' }}>Additional Notes</label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleInputChange}
                rows="3"
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  boxSizing: 'border-box',
                  resize: 'vertical'
                }}
                placeholder="Any special instructions or requests..."
              />
            </div>

            <div className="booking-actions" style={{ 
              marginTop: '30px',
              display: 'flex',
              justifyContent: 'space-between'
            }}>
              <button 
                onClick={prevStep}
                style={{
                  backgroundColor: '#f3f4f6',
                  color: '#374151',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '48%',
                  fontSize: '16px',
                  fontWeight: '500'
                }}
              >
                Back
              </button>
              <button 
                onClick={handleSubmit}
                disabled={!formData.date || !formData.time || submitting}
                style={{
                  backgroundColor: '#4f46e5',
                  color: '#fff',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  width: '48%',
                  fontSize: '16px',
                  fontWeight: '500',
                  opacity: (!formData.date || !formData.time || submitting) ? 0.6 : 1
                }}
              >
                {submitting ? 'Booking...' : 'Book Appointment'}
              </button>
            </div>
          </div>
        )}

        {/* Step 4: Confirmation */}
        {step === 4 && (
          <div className="booking-step confirmation" style={{ textAlign: 'center' }}>
            <div style={{ 
              width: '70px', 
              height: '70px', 
              borderRadius: '50%', 
              backgroundColor: '#10b981',
              color: 'white',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '36px',
              margin: '0 auto 25px',
              fontWeight: 'bold'
            }}>
              ✓
            </div>

            <h2 style={{ color: '#111827', marginBottom: '15px', fontSize: '24px' }}>Booking Confirmed!</h2>
            <p style={{ marginBottom: '25px', color: '#4b5563', fontSize: '16px' }}>
              Your appointment has been scheduled. You will receive a confirmation email shortly.
            </p>

            <div className="booking-summary" style={{
              backgroundColor: '#f3f4f6',
              padding: '20px',
              borderRadius: '8px',
              marginBottom: '25px',
              textAlign: 'left'
            }}>
              <h3 style={{ marginTop: 0, marginBottom: '15px', fontSize: '18px' }}>Booking Summary</h3>
              <p style={{ marginBottom: '10px' }}><strong>Service:</strong> {services.find(s => s._id === formData.serviceId)?.title}</p>
              <p style={{ marginBottom: '10px' }}><strong>Date:</strong> {new Date(formData.date).toLocaleDateString()}</p>
              <p style={{ marginBottom: '10px' }}><strong>Time:</strong> {formData.time}</p>
              <p style={{ marginBottom: '0' }}><strong>Name:</strong> {formData.name}</p>
            </div>

            <button 
              onClick={() => {
                // Reset form and go back to step 1
                setFormData({
                  serviceId: '',
                  name: '',
                  email: '',
                  phone: '',
                  date: '',
                  time: '',
                  notes: '',
                });
                setStep(1);
              }}
              style={{
                backgroundColor: '#4f46e5',
                color: '#fff',
                border: 'none',
                padding: '12px 24px',
                borderRadius: '6px',
                cursor: 'pointer',
                width: '100%',
                fontSize: '16px',
                fontWeight: '500'
              }}
            >
              Book Another Appointment
            </button>
          </div>
        )}
      </div>

      <div className="booking-footer" style={{
        marginTop: '30px',
        textAlign: 'center',
        fontSize: '14px',
        color: '#6b7280'
      }}>
        Powered by DogServices Booking System
      </div>
    </div>
  );
};

export default BookingPage;
