import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import api from '../services/api';

const VenueForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const [loading, setLoading] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    street: '',
    city: '',
    state: '',
    zip_code: '',
    country: 'USA',
    max_capacity: '',
    amenities: [],
    accessibility_features: []
  });

  // Lists for dropdown selections
  const categoryOptions = ['hall', 'meeting', 'banquet', 'auditorium', 'outdoor', 'studio', 'classroom', 'other'];
  const amenityOptions = ['WiFi', 'AV Equipment', 'Kitchen', 'Parking', 'Accessible Entrance', 'Air Conditioning', 'Heating', 'Natural Light', 'Stage', 'Sound System', 'Projector', 'Tables', 'Chairs', 'Whiteboard', 'Bar Area', 'Dance Floor', 'Coat Check', 'Outdoor Space'];
  const accessibilityOptions = ['Wheelchair Access', 'Accessible Restrooms', 'Accessible Parking', 'Hearing Loop', 'Elevator', 'Ramp', 'Braille Signage', 'Service Animal Friendly'];

  // Check if editing existing venue
  const isEditing = Boolean(id);

  // Load venue data if editing
  useEffect(() => {
    if (isEditing) {
      fetchVenue();
    }
  }, [id]);

  const fetchVenue = async () => {
    try {
      setLoading(true);
      const response = await api.getVenue(id);
      const venue = response.data.venue;

      // Set form data from venue
      setFormData({
        name: venue.name || '',
        description: venue.description || '',
        category: venue.category || '',
        street: venue.street || '',
        city: venue.city || '',
        state: venue.state || '',
        zip_code: venue.zip_code || '',
        country: venue.country || 'USA',
        max_capacity: venue.max_capacity || '',
        amenities: venue.amenities || [],
        accessibility_features: venue.accessibility_features || []
      });

      setLoading(false);
    } catch (err) {
      setError('Error loading venue data. Please try again.');
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // Handle number input changes
  const handleNumberChange = (e) => {
    const { name, value } = e.target;
    // Only allow positive numbers
    if (value === '' || parseInt(value) >= 0) {
      setFormData(prevData => ({
        ...prevData,
        [name]: value
      }));
    }
  };

  // Handle multi-select changes
  const handleMultiSelectChange = (name, value) => {
    // Check if item is already selected
    if (formData[name].includes(value)) {
      // Remove it
      setFormData(prevData => ({
        ...prevData,
        [name]: prevData[name].filter(item => item !== value)
      }));
    } else {
      // Add it
      setFormData(prevData => ({
        ...prevData,
        [name]: [...prevData[name], value]
      }));
    }
  };

  // Form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!formData.name || !formData.street || !formData.city || !formData.state || !formData.zip_code || !formData.max_capacity) {
      setError('Please fill in all required fields.');
      return;
    }

    try {
      setIsSubmitting(true);

      // Format data for API
      const venueData = {
        ...formData,
        max_capacity: parseInt(formData.max_capacity)
      };

      if (isEditing) {
        // Update existing venue
        await api.updateVenue(id, venueData);
      } else {
        // Create new venue
        await api.createVenue(venueData);
      }

      // Redirect to venues list
      navigate('/manage-venues');
    } catch (err) {
      console.error('Error submitting venue:', err);
      setError('Error saving venue. Please try again.');
      setIsSubmitting(false);
    }
  };

  if (!user || user.role !== 'venue_owner') {
    return (
      <div className="unauthorized-message">
        <h2>Access Denied</h2>
        <p>You must be a venue owner to access this page.</p>
      </div>
    );
  }

  if (loading) {
    return <div className="loading">Loading venue data...</div>;
  }

  return (
    <div className="venue-form-page">
      <div className="container">
        <div className="page-header">
          <h1>{isEditing ? 'Edit Venue' : 'Add New Venue'}</h1>
        </div>

        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-section">
            <h2>Basic Information</h2>

            <div className="form-group">
              <label htmlFor="name">Venue Name <span className="required">*</span></label>
              <input
                type="text"
                id="name"
                name="name"
                className="form-control"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="category">Category <span className="required">*</span></label>
              <select
                id="category"
                name="category"
                className="form-control"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select a category</option>
                {categoryOptions.map(option => (
                  <option key={option} value={option}>
                    {option.charAt(0).toUpperCase() + option.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                className="form-control"
                value={formData.description}
                onChange={handleChange}
                rows="4"
              ></textarea>
            </div>
          </div>

          <div className="form-section">
            <h2>Location</h2>

            <div className="form-group">
              <label htmlFor="street">Street Address <span className="required">*</span></label>
              <input
                type="text"
                id="street"
                name="street"
                className="form-control"
                value={formData.street}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group col-md-6">
                <label htmlFor="city">City <span className="required">*</span></label>
                <input
                  type="text"
                  id="city"
                  name="city"
                  className="form-control"
                  value={formData.city}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group col-md-3">
                <label htmlFor="state">State <span className="required">*</span></label>
                <input
                  type="text"
                  id="state"
                  name="state"
                  className="form-control"
                  value={formData.state}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group col-md-3">
                <label htmlFor="zip_code">ZIP Code <span className="required">*</span></label>
                <input
                  type="text"
                  id="zip_code"
                  name="zip_code"
                  className="form-control"
                  value={formData.zip_code}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="country">Country</label>
              <input
                type="text"
                id="country"
                name="country"
                className="form-control"
                value={formData.country}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="form-section">
            <h2>Capacity & Features</h2>

            <div className="form-group">
              <label htmlFor="max_capacity">Maximum Capacity <span className="required">*</span></label>
              <input
                type="number"
                id="max_capacity"
                name="max_capacity"
                className="form-control"
                value={formData.max_capacity}
                onChange={handleNumberChange}
                min="1"
                required
              />
            </div>

            <div className="form-group">
              <label>Amenities</label>
              <div className="checkbox-group">
                {amenityOptions.map(option => (
                  <div key={option} className="form-check">
                    <input
                      type="checkbox"
                      id={`amenity-${option}`}
                      className="form-check-input"
                      checked={formData.amenities.includes(option)}
                      onChange={() => handleMultiSelectChange('amenities', option)}
                    />
                    <label htmlFor={`amenity-${option}`} className="form-check-label">
                      {option}
                    </label>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label>Accessibility Features</label>
              <div className="checkbox-group">
                {accessibilityOptions.map(option => (
                  <div key={option} className="form-check">
                    <input
                      type="checkbox"
                      id={`accessibility-${option}`}
                      className="form-check-input"
                      checked={formData.accessibility_features.includes(option)}
                      onChange={() => handleMultiSelectChange('accessibility_features', option)}
                    />
                    <label htmlFor={`accessibility-${option}`} className="form-check-label">
                      {option}
                    </label>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => navigate('/manage-venues')}
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Saving...' : isEditing ? 'Update Venue' : 'Create Venue'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default VenueForm;
