import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import PageHeader from '../components/common/PageHeader';
import LoadingSpinner from '../components/common/LoadingSpinner';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useBusiness } from '../context/BusinessContext';

const BookingPageSetup = () => {
  const { currentUser } = useAuth();
  const { businessProfile } = useBusiness();
  const businessId = businessProfile?._id || '';

  const [loading, setLoading] = useState(true);
  const [copied, setCopied] = useState(false);
  const [services, setServices] = useState([]);

  // Generate the booking page URL
  const getBookingPageUrl = () => {
    const baseUrl = window.location.origin;
    return `${baseUrl}/booking?businessId=${businessId}`;
  };

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await api.getServices();
        setServices(response.data.services || []);
      } catch (error) {
        console.error('Error fetching services:', error);
        toast.error('Failed to load service information');
      } finally {
        setLoading(false);
      }
    };

    if (businessId) {
      fetchServices();
    } else {
      setLoading(false);
    }
  }, [businessId]);

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text)
      .then(() => {
        setCopied(true);
        toast.success('Link copied to clipboard!');
        setTimeout(() => setCopied(false), 3000);
      })
      .catch(err => {
        console.error('Could not copy text: ', err);
        toast.error('Failed to copy link');
      });
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  const bookingPageUrl = getBookingPageUrl();

  return (
    <div className="page-container booking-setup-page">
      <PageHeader 
        title="Booking Page Setup" 
        description="Share your booking page link with clients or add it to your website"
      />

      <div className="card mb-4">
        <div className="card-body">
          <h3 className="mb-3">Your Booking Page</h3>
          <p>
            Your booking page allows clients to schedule appointments directly. 
            You can share this link with clients or add it to your website.
          </p>

          {services.length === 0 ? (
            <div className="alert alert-warning">
              <i className="fas fa-exclamation-triangle mr-2"></i>
              You don't have any services set up yet. Please add some services before sharing your booking page.
            </div>
          ) : (
            <>
              <div className="d-flex align-items-center mt-4 mb-4">
                <div 
                  className="booking-url p-3 bg-light rounded flex-grow-1 mr-2"
                  style={{ 
                    border: '1px solid #dee2e6',
                    overflowX: 'auto',
                    whiteSpace: 'nowrap'
                  }}
                >
                  <a href={bookingPageUrl} target="_blank" rel="noreferrer">{bookingPageUrl}</a>
                </div>
                <button 
                  className="btn btn-primary ml-2"
                  onClick={() => copyToClipboard(bookingPageUrl)}
                >
                  {copied ? 'Copied!' : 'Copy'}
                </button>
              </div>

              <h4 className="mt-4 mb-3">Share Options</h4>
              <div className="row">
                <div className="col-md-6">
                  <div className="card mb-3">
                    <div className="card-body">
                      <h5 className="card-title">
                        <i className="fas fa-link mr-2"></i>
                        Direct Link
                      </h5>
                      <p className="card-text">Share this link directly with your clients via email, text messages, or social media.</p>
                      <button
                        className="btn btn-outline-primary"
                        onClick={() => copyToClipboard(bookingPageUrl)}
                      >
                        Copy Link
                      </button>
                    </div>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="card mb-3">
                    <div className="card-body">
                      <h5 className="card-title">
                        <i className="fas fa-code mr-2"></i>
                        Website Button
                      </h5>
                      <p className="card-text">Add a "Book Now" button to your website that links to your booking page.</p>
                      <div className="mt-2 mb-3">
                        <div 
                          className="bg-light p-2 rounded"
                          style={{ fontSize: '0.9rem', overflowX: 'auto' }}
                        >
                          {`<a href="${bookingPageUrl}" target="_blank" style="display: inline-block; background-color: #4f46e5; color: white; padding: 10px 20px; text-decoration: none; border-radius: 4px; font-family: sans-serif;">Book Now</a>`}
                        </div>
                      </div>
                      <button
                        className="btn btn-outline-primary"
                        onClick={() => copyToClipboard(`<a href="${bookingPageUrl}" target="_blank" style="display: inline-block; background-color: #4f46e5; color: white; padding: 10px 20px; text-decoration: none; border-radius: 4px; font-family: sans-serif;">Book Now</a>`)}
                      >
                        Copy Code
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      </div>

      <div className="card">
        <div className="card-body">
          <h3 className="mb-3">Preview</h3>
          <p>This is what your clients will see when they visit your booking page:</p>

          <div className="booking-preview mt-4" style={{ border: '1px solid #dee2e6', borderRadius: '8px' }}>
            <div style={{ padding: '15px', borderBottom: '1px solid #dee2e6', backgroundColor: '#f8f9fa' }}>
              <h5 style={{ margin: 0 }}>Booking Page Preview</h5>
            </div>
            <div style={{ height: '500px', overflow: 'hidden', position: 'relative' }}>
              <div style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(255,255,255,0.7)', zIndex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
                <h4>Preview Mode</h4>
                <p className="mb-0">This is how your booking page will appear to clients.</p>
                <a 
                  href={bookingPageUrl} 
                  target="_blank" 
                  rel="noreferrer"
                  className="btn btn-primary mt-3"
                >
                  Open actual booking page
                </a>
              </div>
              <iframe 
                src={bookingPageUrl}
                title="Booking Page Preview"
                style={{ border: 'none', width: '100%', height: '100%' }}
              />
            </div>
          </div>

          <div className="alert alert-info mt-4">
            <strong>Tip:</strong> To ensure your clients can book appointments successfully, make sure you have services and availability configured in your account settings.
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingPageSetup;
